import React, { Component } from "react";
import Item from "./Item";
import ActiveItem from "./ActiveItem";

class Table extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    const items = this.props.course_modules.map(data => {
      let handleVideoChange = this.props.handleVideoChange.bind(this, data)
      return data.active ? (
        <ActiveItem
          key={data.id}
          title={data.title}
          description={data.description}
          handleActive={handleVideoChange}
        />
      ) : (
        <Item
          key={data.id}
          title={data.title}
          description={data.description}
          handleActive={handlehandleVideoChange}
        />
      );
    });
    return (
      <div className="pt-5 pb-5">
        <div className="container">
          <div className="text-center">
            <h2 className="pt-4 pb-4">Web Course Template - Modules</h2>
          </div>
          {items}
        </div>
      </div>
    );
  }
}

export default Table;
